body.script:
// Puzzle Types
function genPatternA(i){
  const n = 2 + (i % 20);
  let t="";
  for(let k=0;k<4;k++){
    const x=n+k;
    t+=`${x} + ${x} = ${x*(x+1)}\n`;
  }
  const last=n+4;
  return {text: t+`${last} + ${last} = ??`, answer: String(last*(last+1)), hint:"n + n = n × (n+1)"};
}

function genPatternB(i){
  const n = 3 + (i % 18);
  let t="";
  for(let k=0;k<4;k++){
    const x=n+k;
    t+=`${x} + ${x} = ${x*x}\n`;
  }
  const last=n+4;
  return {text:t+`${last} + ${last} = ??`, answer:String(last*last), hint:"n × n"};
}

function genPatternC(i){
  const a=1+((i*3)%9);
  const b=2+((i*5)%11);
  const t=
`${a} + ${b} = ${a*b}
${a+1} + ${b+1} = ${(a+1)*(b+1)}
${a+2} + ${b+2} = ${(a+2)*(b+2)}
${a+3} + ${b+3} = ??`;
  return {text:t, answer:String((a+3)*(b+3)), hint:"a + b = a × b"};
}

function genPatternD(i){
  const s = 2 + (i % 10);
  let t="";
  for(let k=0;k<4;k++){
    const x=s+k;
    t+=`${x} + ${x+1} = ${x+x+1}\n`;
  }
  const last=s+4;
  return {text:t+`${last} + ${last+1} = ??`, answer:String(last+last+1), hint:"Simple addition"};
}

function genPatternE(i){
  const q=[
    {q:"एक घोड़ा और आधा घोड़ा 3 दिन में 3 खेत जोतते हैं। 1 घोड़ा एक खेत कितने दिनों में जोतेगा?",a:"2"},
    {q:"अगर A>B और B>C तो A और C का रिश्ता क्या होगा?",a:"A>C"},
    {q:"एक भाई के पास 5 आम थे, उसने 2 दिए। कितने बचे?",a:"3"},
    {q:"यदि दिन के बाद रात आती है, तो रात से क्या आती है?",a:"दिन"}
  ];
  const p=q[i%q.length];
  return {text:p.q, answer:p.a, hint:"Logic"};
}

function genPatternF(i){
  const n=2+(i%12);
  let t="";
  for(let k=0;k<4;k++){
    const x=n+k;
    t+=`${x} + ${x} = ${x*(x+2)}\n`;
  }
  const last=n+4;
  return {text:t+`${last} + ${last} = ??`, answer:String(last*(last+2)), hint:"n + n = n × (n+2)"};
}

// Build 1000 puzzles
const gens=[genPatternA,genPatternB,genPatternC,genPatternD,genPatternE,genPatternF];
const puzzles=[];
for(let i=0;i<1000;i++) puzzles.push(gens[i%gens.length](i));

let idx=0, timeLeft=40, interval=null;
const puzzleEl=document.getElementById("puzzle");
const timerEl=document.getElementById("timer");
const answerEl=document.getElementById("answer");
const btn=document.getElementById("showBtn");

function loadPuzzle(){
  const p=puzzles[idx];
  puzzleEl.textContent=p.text;
  answerEl.textContent="";
  startTimer();
}

function startTimer(){
  clearInterval(interval);
  timeLeft=40;
  timerEl.textContent=timeLeft+"s";

  interval=setInterval(()=>{
    timeLeft--;
    timerEl.textContent=timeLeft+"s";
    if(timeLeft<=0){
      clearInterval(interval);
      showAnswer();
      setTimeout(nextPuzzle,1500);
    }
  },1000);
}

function showAnswer(){
  const p=puzzles[idx];
  answerEl.textContent="Answer: "+p.answer+" — ("+p.hint+")";
}

btn.addEventListener("click", ()=>{
  clearInterval(interval);
  showAnswer();
  setTimeout(nextPuzzle,1200);
});

function nextPuzzle(){
  idx=(idx+1)%puzzles.length;
  loadPuzzle();
}

loadPuzzle();
